    DrawingArea,
    Inhibit,
};
use gdk::EventButton;

fn main() {
    // Initialize GTK and create a drawing area
    gtk::init().unwrap();
    let drawing_area = DrawingArea::new();

    // Connect the "button-press-event" signal to a callback function
    drawing_area.connect_button_press_event(move |_, event| {
        // Get the mouse coordinates from the event
        let (x, y) = event.get_position();

        // Print the mouse coordinates
